import { useState } from 'react';
import './App.css';
import { KhmerFont } from './components/khmerfont';

function App() {
  const [previewText, setPreviewText] = useState('သို့ သူငယ်ချင်း သတိရစွာနဲ့ စာရေးလိုက်ပါတယ်။');
  const [fontSize, setFontSize] = useState(16);
  const [lineHeight, setLineHeight] = useState(1);
  return (
    <div className='container mx-auto'>
      <h1 className='text-2xl font-bold mb-2'>Myanmar Font Preview</h1>

      <textarea name='previewText' className='border border-gray-300 rounded-md p-2 mt-2 w-full' value={previewText} onChange={e => setPreviewText(e.target.value)} />

      <div>
      <span className='pr-2'>Font Size:</span>
      <input type='range' min="10" max="32" value={fontSize} id="fontSize" onChange={e => setFontSize(Number(e.target.value))}/>
      <label htmlFor="fontSize" className='pl-2 font-bold'>{fontSize}px</label>
      </div>

      <div>
      <span className='pr-2'>Line Height:</span>
      <input type='range' min="1" max="3" value={lineHeight} id="fontSize" onChange={e => setLineHeight(Number(e.target.value))}/>
      <label htmlFor="fontSize" className='pl-2 font-bold'>{lineHeight}</label>
      </div>
      <KhmerFont previewText={previewText} fontSize={fontSize} lineheight={1.8}/>

    </div>
  );
}

export default App;
