import React from "react";


interface FontBoxProps {
    fontName: string;
    fontSize: number;
    previewText: string;
}

export const FontBox: React.FC<FontBoxProps> = ({ fontName, fontSize, previewText }) => {

    return (
        <div>
        <h2 className="text-sm font-light mb-2">{fontName}</h2>
            <div className={`${fontName}`} style={{fontSize: fontSize + "px", lineHeight: 2}}>
                {previewText}
            </div>
        </div>
    );
};
