import React, { createContext, useState } from 'react';

interface FontContextProps {
    fontSize: number;
    lineHeight: number;
    previewText: string;
    grid: boolean;
    changeFontSize: (newFontSize: number) => void;
    changeLineHeight: (newLineHeight: number) => void;
    changePreviewText: (newPreviewText: string) => void;
    toggleGrid: (gird: boolean) => void;
}

export const FontContext = createContext<FontContextProps | undefined>(undefined);

export const FontProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
    const [color, setColor] = useState<string>('blue');

    const changeColor = (newColor: string) => {
        setColor(newColor);
    };

    const contextValue: FontContextProps = {
        fontSize: 13,
        lineHeight: 1.6,
        previewText: 'ဒီဇင်ဘာည အဖြူရောင်မြူနှင်းကြားမှာ',
        grid: false,
        changeFontSize: (newFontSize: number) => { },
        changeLineHeight: (newLineHeight: number) => { },
        changePreviewText: (newPreviewText: string) => { },
        toggleGrid: (grid: boolean) => { }
    };

    return (
        <FontContext.Provider value={contextValue}>
            {children}
        </FontContext.Provider>
    );
};
