import './App.css';

function App() {

  const [previewText, setPreviewText] = ['သို့ သူငယ်ချင်း သတိရစွာနဲ့ စာရေးလိုက်ပါတယ်။', (value: string) => setPreviewText(value)];
  return (
    <div className='container mx-auto'>
      <h1 className='text-xl font-bold'>Myanmar Font Preview</h1>

      <textarea name='previewText' className='border border-gray-300 rounded-md p-2 mt-2 w-full' value={previewText} onChange={e => setPreviewText(e.target.value)} />
    </div>
  );
}

export default App;
